mapper.select:



select * from (select rownum rn, tt.*
from (select * from board order by ref desc, step asc)tt)
			where rn >=${startNum} and rn <=${endNum}

update board set content=#{content}
				,subject=#{subject}
				,logtime=sysdate where seq = #{seq}

mapper.insert:
begin

	update board set step=step+1 where ref = #{ref} and step > #{step};

	insert into board Values (seq_board.nextval
							, #{id}
							, #{name}
							, #{email}
							, #{subject}
							, #{content}
							, #{ref} 
							, #{lev}+1 
							, #{step}+1 
							, #{pseq}
							, 0 
							, 0 
							,sysdate);

	update board set reply=reply+1 where seq = #{pseq};

end;

mapper.delete:
begin

	update board set reply=reply-1
	where seq=(select pseq from board where seq=#{seq});

	update board set subject='[원글이삭제된답글]'||subject
	where pseq=#{seq};

	delete board where seq = #{seq};
end;

mapper.insert:

	insert into commentText Values(
		seq_comment.nextval
		, #{nickName}
		, #{commentContent}
		, #{seq}
		, seq_comment.nextval
		, 0
		, 0
		, 0
		, 0
		, 0
		,sysdate
)

begin
	insert into commentText Values(
		seq_comment.nextval
		, #{nickName}
		, #{commentContent}
		, #{boardSeq}
		, #{seq}
		, 1
		, 1
		, 0
		, 0
		, 0
		,sysdate
	);
	update commentText set reply=reply+1 where seq = #{seq};

end;
